import React from "react";

const Genres = (props) => {
  return (
    <ul className="list-group">
      {props.genres.map((genre) => (
        <li
          style={{ hover: "pointer" }}
          key={genre._id}
          className={
            genre === props.selectedGenre
              ? "list-group-item active"
              : "list-group-item"
          }
          onClick={() => props.onGenreSelect(genre)}
        >
          {genre.name}
        </li>
      ))}
    </ul>
  );
};

Genres.defaultProps = {
  valueProperty: "_id",
  textProperty: "name",
};

export default Genres;
