import React from "react";

const Genres = ({genres, selectedGenre, onGenreSelect }) => {
  return (
    <ul className="list-group">
      {genres.map((genre) => (
        <li
          key={genre._id}
          className={
            genre === selectedGenre
              ? "list-group-item active"
              : "list-group-item"
          }
          onClick={() => onGenreSelect(genre)}
        >
          {genre[textProperty]}
        </li>
      ))}
    </ul>
  );
};

Genres.defaultProps = {
  valueProperty: "_id",
  textProperty: "name",
};

export default Genres;
